import {useState} from 'react';
import BasicFigure from "./BasicFigure";
import "./FigureList.css";

const intialImages =[
    {src:"https://png.pngtree.com/thumb_back/fh260/background/20240522/pngtree-abstract-cloudy-background-beautiful-natural-streaks-of-sky-and-clouds-red-image_15684333.jpg", caption:"Image 1"},
    {src:"data:image/jpeg;base64,", caption:"Image 2"},
    {src :"data:image/jpeg;base64,", caption:"Image 3"}
]
const FigureList =()=>{
    const [images,setImages]=useState(intialImages);

    const addImage =()=>{
        const randomId =Math.floor(Math.random()*1000)
        const newImage = {
            src :` https://picsum.photos/400/300?random=${randomId}`,
            caption :`Image ${images.length+1}`
        }
        setImages([...images,newImage])
    };
    const removeImage=(index)=>{
        setImages(images.filter((_,i)=>i!==index));
    };
    return(
        <div className ="container">
            <h1>Dyanmic Image Gallery</h1>
            <div classNmae ="buttons">
                <button onClick ={addImage}>Add Images</button>
            </div>
            <div className ='gallery'>
                {images.map((image,index)=>(
                    <BasicFigure key ={index} src ={image.src}
                    caption ={image.caption} onRemove ={()=>removeImage(index)}/>
                ))}
            </div>
        </div>
    )
}
export default FigureList
